import * as React from 'react';
import styles from './Table.module.scss';
import CurrencyFormat from 'react-currency-format';

export default (class Table extends React.PureComponent {
	/*formatData = (data, type) => {
		switch (type) {
			case 'text':
				return data;
			case 'number':
				return data.toLocaleString(navigator.language, { minimumFractionDigits: 0 });
			case 'money':
				return <CurrencyFormat value={data} displayType={'text'} thousandSeparator={true} prefix={'$'} decimalScale={2} fixedDecimalScale={true} />;
			default:
				return data;
		}
	};

	calculateFooter = (data, item) => {
		switch (item.footer) {
			case 'sum':
				return data.reduce((acc, row) => (acc += parseFloat(row[item.value])), 0);
			default:
				return item.footer;
		}
	};*/
/*
<li>Nombre:{response && response.name}</li>
				<li>Temperatura:{response && response.main && response.main.temp}</li>
				<li>Humedad:{response && response.main && response.main.humidity}</li>
				<li>Presion:{response && response.main && response.main.pressure}</li>
*/
	render() {
		const { headers, data } = this.props;
		return (
			<div className={styles.main}>
				<table className={styles.table} border='1'>
					<thead >
						<tr className={styles.header}>
							{headers.map((header, i) => {
								return (
									<th key={i}>
										{header}
									</th>
								);
							})}
						</tr>
					</thead>
					<tbody >
							<tr>
								<td>{data.name}</td>
								<td>{data.main.temp}</td>
								<td>{data.main.humidity}</td>
								<td>{data.main.pressure}</td>
							</tr>

					</tbody>
					<tfoot className={styles.footer}>

					</tfoot>
				</table>
			</div>
		);
	}
});
