import * as React from 'react';
import styles from './Users.module.scss';
import WebServices from '../../WebServices/WebServices';
import Input from '../../components/Input/Input';
import Button from '../../components/Button/Button';

export default (class Users extends React.PureComponent {
	state = {
		response: {},
		countries: [ 'mexico', 'canada', 'usa', 'india', 'china' ],
		countryUrl:"https://restcountries.eu/rest/v2/name/mexico",
		urlClima: ""
	};
//urlClima: "api.openweathermap.org/data/2.5/weather?"
	componentDidMount() {
		//this.fetchData('mexico');
	}

	fetchData = async (country) => {
		try {
			const response = await WebServices.getCountryDetails({
				urlCountries: country
			});
			// const nextState = produce(this.state, (draft) => {
			// 	draft.response = response;
			// });
			// this.setState(nextState);
			this.setState({ response: response[0] });

			console.log('TCL: fetchData -> response', response);
		} catch (e) {}
	};

	showData = (country) => {
		console.log('TCL: showData -> country', country);
		this.fetchData(country);
	};



	onButtonClick = () => {
		const {countryUrl} = this.state;
		console.log('TCL: showData -> country input',countryUrl);
		this.fetchData(countryUrl);
	};

	onInputChange = (event) => {
		console.log('TCL: showData -> country input clima');
		const value = event.target.value;
		this.setState({urlClima:value });
		console.log('TCL: showData -> country input clima',value);
	};

	onClicklist = (tipo) => {
		//const {urlClima} = this.state;
		const tipoURL = tipo;
		const urlWeatherApi = 'http://api.openweathermap.org/data/2.5/weather?id=708546&d0b76fd83718eef1932b224506cfb48f';
		//const keyWeatherApi = 'appid=d0b76fd83718eef1932b224506cfb48f';
		let urlCompuesta = '';
		switch(tipo){
			case 'name':
				urlCompuesta = 'https://restcountries.eu/rest/v2/name/';
				break;
			case 'id':
				urlCompuesta =  urlWeatherApi;
				break;
			case 'coor':
				break;		

		}
		this.setState({urlClima:urlCompuesta });
		//console.log('TCL: showData -> country li clima click',urlClima);
		//this.fetchData(countryUrl,tipoURL);
	};

	render() {
		const { response, countries,countryUrl,urlClima } = this.state;
		return (
			<div className={styles.main}>
				{/*<ul>
					{countries.map((country, i) => {
						return (
							<li key={i} className={styles.country} onClick={() => this.showData(country)}>
								{country}
							</li>
						);
					})}
				</ul>*/
				}
				<div className={styles.contenedor}>
					<label className={styles.label}>Introduce URL de país</label>
					<Input type="text" className={styles.input} value={urlClima} onChange={(event) => this.onInputChange(event)} />
					{/*<Button type={'other'} className={styles.button} label={"GO"} onClick={() => this.onButtonClick()}>GO</Button>*/}
					<ul>
						<li onClick={(event) => this.onClicklist('name')}> by City name</li>
						<li onClick={(event) => this.onClicklist('id')}> by City id</li>
						<li onClick={(event) => this.onClicklist('coor')}> By coordenades</li>
					</ul>	
				</div>	
				<div className={styles.info}>
					<ul>
						<li> País: {response && response.name}</li>
						<li> Capital: {response && response.capital}</li>
						<li> Población: {response && response.population}</li>
						<li>
							Idioma:
							{response &&
								response.languages &&
								response.languages.map((item, i) => {
									return response.languages.length > 1 && i !== response.languages.length - 1 ? ' ' + item.nativeName + ', ' : ' ' + item.nativeName;
								})}
						</li>
						<li> Región: {response && response.region}</li>
						<li>
							Monedas:
							{response &&
								response.currencies &&
								response.currencies.map((item, i) => {
									return response.currencies.length > 1 && i !== response.currencies.length - 1 ? ' ' + item.name + ', ' : ' ' + item.name;
								})}
						</li>
					</ul>
					<img className={styles.image} src = {response && response.flag}/> 
				</div>
			</div>
		);
	}
});
