import * as React from 'react';
import styles from './Users.module.scss';
import WebServices from '../../WebServices/WebServices';
import Input from '../../components/Input/Input';
import Button from '../../components/Button/Button';
import Table from '../../components/Table/Table';
import SimpleBarChart from '../../components/Chart/SimpleBarChart';

export default (class Users extends React.PureComponent {
	state = {
		response: {},
		countries: [ 'mexico', 'canada', 'usa', 'india', 'china' ],
		countryUrl:"https://restcountries.eu/rest/v2/name/mexico",
		urlClima: "",
		selected:true,
		datos:[]
	};

	componentDidMount() {
		/*const {urlClima} = this.state;
		urlClima && this.fetchData(urlClima);*/
	}

	fetchData = async (urlCity) => {
		try {
			const response = await WebServices.getWeather({
				urlCity
			});
			this.setState({ response: response });

			console.log('fetch data response', response);
		} catch (e) {}
	};

	onInputChange = (event) => {
		const value = event.target.value;
		this.setState({urlClima:value});
		console.log('input',value);
	};

	onButtonNew = () => {
		//this.componentDidMount();
		const {datos,urlClima,response} = this.state;
		console.log('response',response);
		/*datos.push(response);

		this.setState({datos:datos});*/

	};

	onClicklist = (tipo) => {
		let urlCompuesta = '';
		switch(tipo){
			case 'name':
				urlCompuesta = 'http://api.openweathermap.org/data/2.5/weather?q=London&appid=d0b76fd83718eef1932b224506cfb48f';
				break;
			case 'id':
				urlCompuesta =  'http://api.openweathermap.org/data/2.5/weather?id=708546&appid=d0b76fd83718eef1932b224506cfb48f';
				break;
			case 'coor':
				urlCompuesta = 'http://api.openweathermap.org/data/2.5/weather?lat=35&lon=139&appid=d0b76fd83718eef1932b224506cfb48f';
				break;		

		}
		this.setState({urlClima:urlCompuesta });
		this.fetchData(urlCompuesta);
	};

	render() {

		const { response, urlClima,datos } = this.state;
		const headers = ['city','temp','humidity','pressure'];
		const data = datos;
		console.log('TCL: showData -> country input datos',datos);
		return (
			<div className={styles.main}>

				<div className={styles.contenedor}>
					<label className={styles.label}>Introduce URL de pa√≠s</label>
					<Input type="text" className={styles.input} value={urlClima} onChange={(event) => this.onInputChange(event)} />
					<Button type={'other'} className={styles.button} label={"new"} onClick={() => this.onButtonNew()}/>
					<ul>
						<li onClick={(event) => this.onClicklist('name')}> by City name</li>
						<li onClick={(event) => this.onClicklist('id')}> by City id</li>
						<li onClick={(event) => this.onClicklist('coor')}> By coordenades</li>
					</ul>	
				</div>	

				<div className={styles.info}>
					<ul>
						<li>Nombre:{response && response.name}</li>
						<li>Temperatura:{response && response.main && response.main.temp}</li>
						<li>Humedad:{response && response.main && response.main.humidity}</li>
						<li>Presion:{response && response.main && response.main.pressure}</li>
					</ul>
				</div>
				<div>
					<Table data={data} headers={headers} />
					<SimpleBarChart data={data} x={'zone'} y1={'sold'} y2={'total'} y1Axis={'left'} y2Axis={'right'} />
				</div>
			</div>
		);
	}
});
